import React from "react";

const MOCK_WEATHER = [
  {
    name: "Chennai",
    sys: { country: "IN" },
    main: { temp: 33, humidity: 70 },
    weather: [{ main: "Clouds", description: "scattered clouds" }],
    wind: { speed: 4.2 },
  },
  {
    name: "Coimbatore",
    sys: { country: "IN" },
    main: { temp: 29, humidity: 65 },
    weather: [{ main: "Clear", description: "clear sky" }],
    wind: { speed: 3.1 },
  },
  {
    name: "Madurai",
    sys: { country: "IN" },
    main: { temp: 31, humidity: 60 },
    weather: [{ main: "Rain", description: "light rain" }],
    wind: { speed: 5.0 },
  },
  {
    name: "Salem",
    sys: { country: "IN" },
    main: { temp: 30, humidity: 68 },
    weather: [{ main: "Clouds", description: "broken clouds" }],
    wind: { speed: 2.8 },
  },
  {
    name: "Tirunelveli",
    sys: { country: "IN" },
    main: { temp: 32, humidity: 72 },
    weather: [{ main: "Clear", description: "sunny" }],
    wind: { speed: 3.5 },
  },
];

function Exercise7() {
  return (
    <div className="container" style={{ maxWidth: 700, margin: "40px auto" }}>
      <h2 style={{ textAlign: "center" }}>Weather in 5 Districts</h2>
      <div style={{ display: "flex", flexWrap: "wrap", gap: 20, justifyContent: "center" }}>
        {MOCK_WEATHER.map((data) => (
          <div key={data.name} className="card" style={{ width: 200, textAlign: "center", padding: 16 }}>
            <h3>
              {data.name}, {data.sys.country}
            </h3>
            <div style={{ fontSize: 32 }}>{Math.round(data.main.temp)}Â°C</div>
            <div>{data.weather[0].main}</div>
            <div style={{ fontSize: 12, color: "#666" }}>{data.weather[0].description}</div>
            <div>Humidity: {data.main.humidity}%</div>
            <div>Wind: {data.wind.speed} m/s</div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Exercise7;
