import React, { useState } from 'react';
import { Link } from 'react-router-dom';

// User Profile Component using props
const UserProfile = ({ user, onEditProfile }) => (
  <div className="profile-card">
    <div className="profile-header">
      <img 
        src={user.avatar} 
        alt={user.name}
        className="profile-avatar"
        onError={(e) => {
          e.target.src = `https://ui-avatars.com/api/?name=${user.name}&size=120&background=667eea&color=fff`;
        }}
      />
      <h2 style={{ color: '#333', marginBottom: '8px' }}>{user.name}</h2>
      <p style={{ color: '#666', fontSize: '1.1rem' }}>{user.title}</p>
    </div>
    
    <div className="profile-info">
      <div className="profile-field">
        <span className="profile-label">Email:</span>
        <span className="profile-value">{user.email}</span>
      </div>
      
      <div className="profile-field">
        <span className="profile-label">Phone:</span>
        <span className="profile-value">{user.phone}</span>
      </div>
      
      <div className="profile-field">
        <span className="profile-label">Location:</span>
        <span className="profile-value">{user.location}</span>
      </div>
      
      <div className="profile-field">
        <span className="profile-label">Company:</span>
        <span className="profile-value">{user.company}</span>
      </div>
      
      <div className="profile-field">
        <span className="profile-label">Department:</span>
        <span className="profile-value">{user.department}</span>
      </div>
      
      <div className="profile-field">
        <span className="profile-label">Experience:</span>
        <span className="profile-value">{user.experience} years</span>
      </div>
      
      <div className="profile-field">
        <span className="profile-label">Skills:</span>
        <span className="profile-value">
          {user.skills.map((skill, index) => (
            <span 
              key={index}
              style={{
                background: '#667eea',
                color: 'white',
                padding: '4px 8px',
                borderRadius: '12px',
                fontSize: '0.8rem',
                margin: '0 4px 4px 0',
                display: 'inline-block'
              }}
            >
              {skill}
            </span>
          ))}
        </span>
      </div>
      
      <div className="profile-field">
        <span className="profile-label">Bio:</span>
        <span className="profile-value" style={{ textAlign: 'justify' }}>
          {user.bio}
        </span>
      </div>
    </div>
    
    <div style={{ textAlign: 'center', marginTop: '32px' }}>
      <button className="btn" onClick={onEditProfile}>
        Edit Profile
      </button>
    </div>
  </div>
);

// Profile Edit Form Component
const ProfileEditForm = ({ user, onSave, onCancel }) => {
  const [formData, setFormData] = useState(user);

  const handleChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        setFormData(prev => ({
          ...prev,
          avatar: event.target.result
        }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(formData);
  };

  return (
    <div className="profile-card">
      <h2 style={{ textAlign: 'center', marginBottom: '32px', color: '#333' }}>
        Edit Profile
      </h2>
      
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '20px', textAlign: 'center' }}>
          <div style={{ marginBottom: '16px' }}>
            <img 
              src={formData.avatar} 
              alt="Profile Preview"
              style={{
                width: '120px',
                height: '120px',
                borderRadius: '50%',
                objectFit: 'cover',
                border: '4px solid #667eea',
                margin: '0 auto 16px'
              }}
              onError={(e) => {
                e.target.src = `https://ui-avatars.com/api/?name=${formData.name}&size=120&background=667eea&color=fff`;
              }}
            />
          </div>
          <div>
            <label style={{ display: 'block', marginBottom: '8px', fontWeight: '600', color: '#333' }}>
              Profile Image:
            </label>
            <input
              type="file"
              accept="image/*"
              onChange={handleImageChange}
              style={{
                width: '100%',
                padding: '8px',
                border: '2px solid #e9ecef',
                borderRadius: '8px',
                fontSize: '16px'
              }}
            />
            <p style={{ fontSize: '12px', color: '#666', marginTop: '4px' }}>
              Supported formats: JPG, PNG, GIF
            </p>
          </div>
        </div>
        
        <div style={{ marginBottom: '20px' }}>
          <label style={{ display: 'block', marginBottom: '8px', fontWeight: '600', color: '#333' }}>
            Name:
          </label>
          <input
            type="text"
            value={formData.name}
            onChange={(e) => handleChange('name', e.target.value)}
            style={{
              width: '100%',
              padding: '12px',
              border: '2px solid #e9ecef',
              borderRadius: '8px',
              fontSize: '16px'
            }}
            required
          />
        </div>
        
        <div style={{ marginBottom: '20px' }}>
          <label style={{ display: 'block', marginBottom: '8px', fontWeight: '600', color: '#333' }}>
            Title:
          </label>
          <input
            type="text"
            value={formData.title}
            onChange={(e) => handleChange('title', e.target.value)}
            style={{
              width: '100%',
              padding: '12px',
              border: '2px solid #e9ecef',
              borderRadius: '8px',
              fontSize: '16px'
            }}
            required
          />
        </div>
        
        <div style={{ marginBottom: '20px' }}>
          <label style={{ display: 'block', marginBottom: '8px', fontWeight: '600', color: '#333' }}>
            Email:
          </label>
          <input
            type="email"
            value={formData.email}
            onChange={(e) => handleChange('email', e.target.value)}
            style={{
              width: '100%',
              padding: '12px',
              border: '2px solid #e9ecef',
              borderRadius: '8px',
              fontSize: '16px'
            }}
            required
          />
        </div>
        
        <div style={{ marginBottom: '20px' }}>
          <label style={{ display: 'block', marginBottom: '8px', fontWeight: '600', color: '#333' }}>
            Phone:
          </label>
          <input
            type="tel"
            value={formData.phone}
            onChange={(e) => handleChange('phone', e.target.value)}
            style={{
              width: '100%',
              padding: '12px',
              border: '2px solid #e9ecef',
              borderRadius: '8px',
              fontSize: '16px'
            }}
            required
          />
        </div>
        
        <div style={{ marginBottom: '20px' }}>
          <label style={{ display: 'block', marginBottom: '8px', fontWeight: '600', color: '#333' }}>
            Location:
          </label>
          <input
            type="text"
            value={formData.location}
            onChange={(e) => handleChange('location', e.target.value)}
            style={{
              width: '100%',
              padding: '12px',
              border: '2px solid #e9ecef',
              borderRadius: '8px',
              fontSize: '16px'
            }}
            required
          />
        </div>
        
        <div style={{ marginBottom: '20px' }}>
          <label style={{ display: 'block', marginBottom: '8px', fontWeight: '600', color: '#333' }}>
            Bio:
          </label>
          <textarea
            value={formData.bio}
            onChange={(e) => handleChange('bio', e.target.value)}
            rows="4"
            style={{
              width: '100%',
              padding: '12px',
              border: '2px solid #e9ecef',
              borderRadius: '8px',
              fontSize: '16px',
              resize: 'vertical'
            }}
            required
          />
        </div>
        
        <div style={{ display: 'flex', gap: '16px', justifyContent: 'center' }}>
          <button type="submit" className="btn">
            Save Changes
          </button>
          <button type="button" className="btn" onClick={onCancel} style={{
            background: '#dc3545',
            border: 'none'
          }}>
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

// User Stats Component
const UserStats = ({ user }) => (
  <div style={{
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
    gap: '20px',
    marginBottom: '32px'
  }}>
    <div className="card" style={{ textAlign: 'center' }}>
      <div style={{ fontSize: '2rem', color: '#667eea', marginBottom: '8px' }}>üìä</div>
      <div style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#333' }}>
        {user.projects} Projects
      </div>
    </div>
    
    <div className="card" style={{ textAlign: 'center' }}>
      <div style={{ fontSize: '2rem', color: '#667eea', marginBottom: '8px' }}>‚≠ê</div>
      <div style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#333' }}>
        {user.rating} Rating
      </div>
    </div>
    
    <div className="card" style={{ textAlign: 'center' }}>
      <div style={{ fontSize: '2rem', color: '#667eea', marginBottom: '8px' }}>üèÜ</div>
      <div style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#333' }}>
        {user.awards} Awards
      </div>
    </div>
  </div>
);

function Exercise2() {
  const [isEditing, setIsEditing] = useState(false);
  const [currentUser, setCurrentUser] = useState({
    name: "John Doe",
    title: "Senior React Developer",
    email: "john.doe@example.com",
    phone: "+1 (555) 123-4567",
    location: "San Francisco, CA",
    company: "TechCorp Inc.",
    department: "Engineering",
    experience: 5,
    avatar: "https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=120&h=120&fit=crop&crop=face",
    skills: ["React", "JavaScript", "TypeScript", "Node.js", "CSS", "HTML"],
    bio: "Passionate React developer with 5+ years of experience building modern web applications. I love creating user-friendly interfaces and solving complex problems with clean, maintainable code.",
    projects: 25,
    rating: 4.8,
    awards: 3
  });

  const handleEditProfile = () => {
    setIsEditing(true);
  };

  const handleSaveProfile = (updatedUser) => {
    setCurrentUser(updatedUser);
    setIsEditing(false);
  };

  const handleCancelEdit = () => {
    setIsEditing(false);
  };

  return (
    <div className="container">
      <div className="card">
        <h1 style={{ textAlign: 'center', marginBottom: '24px', color: '#333' }}>
          Exercise 2: User Profile with Props
        </h1>
        <p style={{ textAlign: 'center', fontSize: '18px', color: '#666', marginBottom: '32px' }}>
          This exercise demonstrates the use of React props to create a dynamic user profile page.
        </p>
        
        <UserStats user={currentUser} />
        
        {isEditing ? (
          <ProfileEditForm 
            user={currentUser}
            onSave={handleSaveProfile}
            onCancel={handleCancelEdit}
          />
        ) : (
          <UserProfile 
            user={currentUser}
            onEditProfile={handleEditProfile}
          />
        )}
        
        <div style={{ textAlign: 'center', marginTop: '32px' }}>
          <Link to="/" className="btn">
            Back to Home
          </Link>
        </div>
      </div>
    </div>
  );
}

export default Exercise2;
